  )
  shiny::observeEvent(input$negative_control_outcomes_estimate_name,
                      {
                        updateButtons$negative_control_outcomes <- TRUE
                      },
                      ignoreInit = TRUE
  )
  shiny::observeEvent(input$negative_control_outcomes_outcome_group,
