    getNegativeControlOutcomesData() |>
      omopgenerics::tidy() |>
      dplyr::select(dplyr::any_of(c(input$negative_control_outcomes_tidy_columns, "exp_coef", "lower_ci", "upper_ci", "median", "q25", "q75", "min", "max", "count"))) |>
      DT::datatable(
        filter = "top",
        rownames = FALSE,
        options = list(searching = FALSE)
      )
  })
